-- Add category column to members table
ALTER TABLE public.members 
ADD COLUMN category text NOT NULL DEFAULT 'member';

-- Add check constraint for category values
ALTER TABLE public.members 
ADD CONSTRAINT check_member_category 
CHECK (category IN ('member', 'expert'));

-- Insert council members
INSERT INTO public.members (name_bg, position_bg, photo_url, biography_bg, category, order_index, active) VALUES
('Симеон Дянков', 'Председател на Фискалния съвет', 'https://www.fiscal-council.bg/medias/uploads/team/2025/simeon-dyankov.jpg', 'Симеон Дянков става магистър по икономика в Мичиганския университет през 1995 г., а през 1997 г. получава научната степен доктор по икономика от същия университет. Той е министър на финансите на България между 2009 и 2013 г., а след това е избран за председател на Борда на директорите на Европейската банка за възстановяване и развитие. Симеон Дянков е сред първите 1% икономисти в света по брой публикации и цитирания в класацията на RePEc. Симеон Дянков e #194 в световната листа на най-изтъкнатите икономисти и финансисти към октомври 2023 година, #23 в Обединеното Кралство и #1 в България. Дянков има 70 хиляди цитирания в научни издания.', 'member', 1, true),
('Проф. д-р Богомил Борисов Манов', 'Член на Фискалния съвет', 'https://www.fiscal-council.bg/medias/uploads/team/2025/bogomil-manov-2.jpg', 'Проф. д-р Богомил Борисов Манов завършил висшето си образование в СА Д.А. Ценов гр. Свищов. От 1985 г. е преподавател в УНСС, като за периода от 2012 г. до 2024 година е и преподавател в ПУ Паисий Хилендарски. Чете лекции по Основи на финансите, Корпоративни финанси и Финанси на социалното осигуряване. Има две издадени книги, над 60 публикувани статии и доклади. Проф. д-р Манов има 16 годишен практически стаж. Управлявал е две дружества с ограничена отговорност, предприятие в чужбина и две пенсионни компании.', 'member', 2, true),
('Д-р Десислава Калчева', 'Член на Фискалния съвет', 'https://www.fiscal-council.bg/medias/uploads/team/2025/desislava-kalcheva.jpg', 'Десислава Калчева придобива магистърска степен по Публични финанси в УНСС през 2010 година. През 2016 г. защитава дисертационен труд на тема Фискална децентрализация и инвестиционен капацитет на общините в България, като придобива докторска степен по Финанси. Д-р Калчева е преподавател в катедра Икономика и управление по отрасли на Стопански факултет на СУ Св. Климент Охридски. Притежава повече от 15 години опит в сферата на публичните финанси, работила е като експерт по общински финанси във Фонд за органите на местното самоуправление в България (ФЛАГ) и като главен експерт към Фискалния съвет на България.', 'member', 3, true),
('Атанас Атанасов', 'Член на Фискалния съвет', 'https://www.fiscal-council.bg/medias/uploads/team/2025/atanas-atanasov.jpg', 'Атанас Атанасов е магистър по Финанси от УНСС, гр. София. Придобива също магистратура по икономика от Фламандския университет Vrije Universiteit Brussel и магистратура Счетоводство и контрол от Стопанска академия Д. А. Ценов, гр. Свищов. Притежава сертификат Вътрешен одитор в публичния сектор. Заемал е различни длъжности в държавната администрация, като началник Финансов контрол, счетоводител и финансист в екипи за управление на инфраструктурни проекти по оперативните програми, финансирани със средства от ЕС. Атанас Атанасов е във Фискалния съвет от самото му създаване, полагайки основите на тази нова за България институцията.', 'member', 4, true),
('Любомир Дацов', 'Член на Фискалния съвет', 'https://www.fiscal-council.bg/medias/uploads/team/2025/l.datzov.jpg', 'Любомир Дацов е бивш зам. финансов министър, отговарял за бюджета, структурните реформи и координацията на еврофондовете. Бил е член на икономическия и финансов комитет към съвета на финансовите министри в ЕС. Инициатор и координатор на въвеждането на програмното бюджетиране в България. Работил е за Министерство на финансите от 1992-2009г. От 2009 г. е консултант по макроикономически, финансови и банкови въпроси. Бил е хоноруван асистент по публични финанси към стопанския факултет на СУ Климент Охридски. Редовно публикува анализи в различни медии. Завършил е УНСС- София и има редица специализации по макроикономика и бюджетен мениджмънт.', 'member', 5, true);

-- Insert expert council members
INSERT INTO public.members (name_bg, position_bg, photo_url, biography_bg, category, order_index, active) VALUES
('Дияна Металова', 'Главен експертен сътрудник', 'https://www.fiscal-council.bg/medias/uploads/team/2025/diana-metalova.jpg', 'Дияна Металова е главен експертен сътрудник към Фискалния съвет от 2018г., където участва активно при подготовката на становищата на Съвета по отношение на провежданата фискална политика в страната. Същевременно е преподавател в катедра Финанси към УНСС от 2009г. Научните й интереси са в областта на публичните финанси, анализът разходи-ползи, фискалната децентрализация, данъчното облагане. Завършва специалност Финанси в УНСС като последователно придобива бакалавърска, магистърска и докторска степен. През 2007г. придобива и магистърска степен по Право в ЮЗУ Неофит Рилски. Професионалният й опит започва през 2005г. в Министерство на финансите, а в периода 2011-2016г. работи като главен експерт по европейски програми и проекти в Министерство на околната среда и водите. Притежава солиден опит като консултант при изготвянето на анализи на разходите и ползите по европейски проекти.', 'expert', 1, true),
('Петър Игнатиев', 'Главен експертен сътрудник', 'https://www.fiscal-council.bg/medias/uploads/team/2025/petur-ignatev.jpg', 'Роден в София 13.09.1967 г. Завършил с отличие Софийска Математическа Гимназия и УНСС специалност Финанси. Защитил докторат по финанси в Икономическия институт на БАН на тема Българската банкова система през 90-те години на ХХ век. Работил в БНБ през 1992-1998 г. като анализатор в дирекция Икономически и паричен анализ и управление Банков надзор. Работил в ОББ през 1999-2024 г. като анализатор в дирекции Главен Икономист и Управление на риска. Обучение в Federal Reserve Board, Washington, Bank of England, London и JVI- Vienna.', 'expert', 2, true);